mapper.select:
select 
count(*)
from sys_warn_log
where 1=1

  and WARN_MODULE = #{warnModule}


  and WARN_LEVEL = #{warnLevel}


  and STATUS = #{status}

select 

from sys_warn_log
where 1=1

  and WARN_MODULE = #{warnModule}


  and WARN_LEVEL = #{warnLevel}


  and STATUS = #{status}

order by WARN_TIME desc
limit #{start}, #{length}
