mapper.select:
select 
count(*)
from ac_action
where 1=1 

  and STATUS=#{status}


  and OSS_KEY=#{ossKey}


  and BUSINESS_TYPE=#{businessType}


  and BUSINESS_KEY like concat(#{businessKey}, '%')

select 

,(select NAME from ref_oss_key where ref_oss_key.ID=OSS_KEY) AS OSS_KEY_NAME
from ac_action
where 1=1 

  and STATUS=#{status}


  and OSS_KEY=#{ossKey}


  and BUSINESS_TYPE=#{businessType}


  and BUSINESS_KEY like concat(#{businessKey}, '%')

order by CREATE_TIME desc
limit #{start}, #{length}

mapper.update:
update ac_action
set 
  STATUS = #{1}
where JOB_ID = #{0}
